import { mapState, mapActions } from "vuex";
import { localStorageUtil } from "@/util.js";

export default {
  data() {
    return {
      pageindex: 0,
      timer: null
    };
  },
  methods: {
    ...mapActions({
      getInfo: "main/getInfo",
      getVideoList: "main/getVideoList",
      testCDN: "cdn/testCDN"
    }),
    showDetail(movie) {
      //  console.log(movie)
      localStorageUtil.create("currentID", movie.id);

      this.$router.push("/detail/" + movie.id);
    },
    nextPage() {
      this.pageindex += 1;
      const params = {
        page: this.pageindex
      };
      this.getVideoList(params);
      this.backTop();
    },
    previous() {
      if (this.pageindex == 0) {
        return;
      } else {
        this.pageindex -= 1;
        const params = {
          page: this.pageindex
        };
        this.getVideoList(params);
      }
      this.backTop();
    },
    CDN() {
      if (this.timer) {
        clearInterval(this.timer);
      } else {
        this.timer = setInterval(() => {
          this.testCDN();
        }, 60000);
      }
    },

    backTop() {
      const that = this;
      let timer = setInterval(() => {
        let ispeed = Math.floor(-that.scrollTop / 5);
        document.documentElement.scrollTop = document.body.scrollTop =
          that.scrollTop + ispeed;
        if (that.scrollTop === 0) {
          clearInterval(timer);
        }
      }, 16);
    },
    scrollToTop() {
      const that = this;
      let scrollTop =
        window.pageYOffset ||
        document.documentElement.scrollTop ||
        document.body.scrollTop;
      that.scrollTop = scrollTop;
      if (that.scrollTop > 60) {
        that.btnFlag = true;
      } else {
        that.btnFlag = false;
      }
    }
  },
  computed: {
    ...mapState({
      infoList: state => state.main.infoList,
      videoList: state => state.main.videoList,
      cdn: state => state.cdn.cdn
    })
  },
  created() {
    this.getInfo();
    const params = {
      page: 0
    };
    this.getVideoList(params);
    console.log(this.infoList.data);
  },
  mounted() {
    window.addEventListener("scroll", this.scrollToTop);
    this.testCDN();
    this.CDN();

    const a = localStorageUtil.read("key");
    console.log(a);
  },
  destroyed() {
    window.removeEventListener("scroll", this.scrollToTop);
    clearInterval(this.timer);
  }
};
